import React, { useState, useEffect } from 'react';
import { isDesktop, isIPad13, isTablet, isMobile, osName, browserName } from 'react-device-detect';
import './App.css';
import logoPng from './Images/png/Palatial-Logo_White 1.png';

function App() {
  const [userName, setUserName] = useState('');
  const [device, setDevice] = useState('Desktop');

  useEffect(() => {
    if (isMobile && !isIPad13 ) {
      setDevice('Mobile');
    } else if (isIPad13 || isTablet) {
      setDevice('Tablet');
    } else if (isDesktop) {
      setDevice('Desktop');
    }

    if (isMobile || isTablet || isIPad13) {
      document.body.style.height = `${window.innerHeight}px`;
      window.addEventListener('touchmove', function (event) {
        event.preventDefault();
      }, { passive: false });
    }
  }, []);

  const style = (isMobile || isTablet || isIPad13 ) ? { height: '100%', overflow: 'hidden' } : {};

  return (
    <div className="App">
      <div className = "PopUp">
        <div className = "Logo">
          <img src={logoPng} alt='logo'/>
        </div>
        <div className = "textField">
          <p>ENTER YOUR NAME</p>
          <label>
            <input 
              className="textInput"
              type="text" 
              value={userName} 
              onChange={(e) => setUserName(e.target.value)} 
              required
            />
          </label>
        </div>
      </div>
      <p>You are on a {device} device using {browserName} on {osName}.</p>
    </div>
  );
}

export default App;
