import React, { useState, useEffect } from 'react';
import { isDesktop, isIPad13, isTablet, isMobile, osName, browserName } from 'react-device-detect';
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [device, setDevice] = useState('Desktop');

  useEffect(() => {
    if (isMobile && !isIPad13 ) {
      setDevice('Mobile');
    } else if (isIPad13 || isTablet) {
      setDevice('Tablet');
    } else if (isDesktop) {
      setDevice('Desktop');
    }

    if (isMobile || isTablet || isIPad13) {
      document.body.style.height = `${window.innerHeight}px`;
      window.addEventListener('touchmove', function (event) {
        event.preventDefault();
      }, { passive: false });
    }
  }, []);

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    const output = {
      email: email,
      timestamp: Math.floor(Date.now() / 1000),
      device: device,
      screenResolution: `${window.innerWidth}x${window.innerHeight}`,
      browser: browserName,
      operatingSystem: osName,
    };

    console.log(output);

    const jsonOutput = JSON.stringify(output);

    const url = 'https://palatial.tenant-palatial-platform.coreweave.cloud';  

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: jsonOutput,
      });

      const data = await response.json();

      console.log(data);

    } catch (error) {
      console.error('Error:', error);
    }
  }

  const style = (isMobile || isTablet || isIPad13 ) ? { height: '100%', overflow: 'hidden' } : {};

  return (
    <div className="App" style={style}>
      <form onSubmit={handleFormSubmit}>
        <h2>Login</h2>
        <label>
          Email
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required/>
        </label>
        <button type="submit">Submit</button>
      </form>
      <p>You are on a {device} device using {browserName} on {osName}.</p>
    </div>
  );
}

export default App;
