import React, { useState, useEffect } from 'react';
import { isDesktop, isIPad13, isTablet, isMobile, osName, browserName } from 'react-device-detect';
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [device, setDevice] = useState('Desktop');
  const [msgColor, setMsgColor] = useState('black');

  useEffect(() => {
    if (isMobile && !isIPad13 ) {
      setDevice('Mobile');
    } else if (isIPad13 || isTablet) {
      setDevice('Tablet');
    } else if (isDesktop) {
      setDevice('Desktop');
    }

    if (isMobile || isTablet || isIPad13) {
      document.body.style.height = `${window.innerHeight}px`;
      window.addEventListener('touchmove', function (event) {
        event.preventDefault();
      }, { passive: false });
    }
  }, []);

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    const passwordPattern = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{10,30}$/;

    if (!passwordPattern.test(password)) {
      setMessage('Password must be 10-30 characters and include 1 uppercase, 1 number, and 1 special character.');
    } else {
      setMessage('');

      const output = {
        email: email,
        password: password,
        timestamp: Math.floor(Date.now() / 1000),
        device: device,
        screenResolution: `${window.innerWidth}x${window.innerHeight}`,
        browser: browserName,
        operatingSystem: osName,
      };

      console.log(output);

      const jsonOutput = JSON.stringify(output);

      const url = 'https://palatial.tenant-palatial-platform.coreweave.cloud';  

      try {
        const response = await fetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: jsonOutput,
        });

        const data = await response.json();

        if (data.message === 'password is incorrect') {
          setMessage('Password is incorrect. Please try again.');
          setMsgColor('red');
        } else {
          console.log(data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    }
  }

  const style = (isMobile || isTablet || isIPad13 ) ? { height: '100%', overflow: 'hidden' } : {};

  return (
    <div className="App">
      <div className="App-content">
      <form onSubmit={handleFormSubmit}>
        <h2>Login</h2>
        <label>
          Email
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required/>
        </label>
        <label>
          Password
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required/>
        </label>
        <button type="submit">Submit</button>
      </form>
      <p style={{ color: msgColor }}>You are on a {device} device using {browserName} on {osName}.</p>
      </div>
      </div>
  );
}

export default App;
