import React, { useState, useEffect } from 'react';
import { isDesktop, isIPad13, isTablet, isMobile } from 'react-device-detect';
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [device, setDevice] = useState('Desktop');

  useEffect(() => {
    if (isMobile && !isIPad13 ) {
      setDevice('Mobile');
    } else if (isIPad13 || isTablet) {
      setDevice('Tablet');
    } else if (isDesktop) {
      setDevice('Desktop');
    }

    if (isMobile || isTablet || isIPad13) {
      // Set the height of the body to the height of the window
      document.body.style.height = `${window.innerHeight}px`;

      // Disable pull-to-refresh on most mobile and tablet browsers
      window.addEventListener('touchmove', function (event) {
        event.preventDefault();
      }, { passive: false });
    }
  }, []);

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    const passwordPattern = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{10,30}$/;

    if (!passwordPattern.test(password)) {
      setMessage('Password must be 10-30 characters and include 1 uppercase, 1 number, and 1 special character.');
    } else {
      setMessage('');

      // JSON output
      const output = {
        email: email,
        password: password,
        timestamp: Math.floor(Date.now() / 1000),
      };

      console.log(output);

      // Convert the output object to a string
      const jsonOutput = JSON.stringify(output);

      // Send data to a server
      const url = 'https://your-target-url.com';  // Replace with your target URL

      try {
        const response = await fetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: jsonOutput,
        });

        const data = await response.json();

        if (data.message === 'password is incorrect') {
          setMessage('Password is incorrect. Please try again.');
          setMsgColor('red');
        } else {
          console.log(data);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    }
  }

  const style = (isMobile || isTablet || isIPad13 ) ? { height: '100%', overflow: 'hidden' } : {};

  return (
    <div className="App" style={style}>
      <form onSubmit={handleFormSubmit}>
        <h2>Login</h2>
        <label>
          Email
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required/>
        </label>
        <label>
          Password
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required/>
        </label>
        <button type="submit">Submit</button>
      </form>
      <p>{message}</p>
      <p>You are on a {device} device.</p>
    </div>
  );
}

export default App;
