    function create(config, delegate) {
        return new libspsfrontend
            .webRtcPlayerController(config,
                delegate);
    }
    /*
    document.addEventListener("touchmove", (event: TouchEvent) => {
        event.preventDefault();
    }, { passive: false });
    */
    // Create a config object instance
    function CreateConfig(signalingAddress,
        playerElement) {
        var config = new libspsfrontend
            .Config(signalingAddress,
                playerElement, isMobile());
        return config;
    }
