            // Hide the 'edit text' button.
            editTextButton.classList.add(
                'hiddenState');
            editTextButton.addEventListener(
                "touchend", (function (e) {
                    hiddenInput.focus();
                    hiddenInput
                        .setSelectionRange(
                            hiddenInput.value
                                .length, hiddenInput
                                    .value.length);
                    editTextButton.classList
                        .add('hiddenState');
                    e.preventDefault();
                }));
        }
        libspsfrontend.DataChannelController
            .hiddenInput = hiddenInput;
        libspsfrontend.DataChannelController
            .editTextButton = editTextButton;
    }

    function isMobile() {
        var check = false;
