import React, { useState, useEffect } from 'react';
import { isDesktop, isIPad13, isTablet, isMobile, osName, browserName } from 'react-device-detect';
import './App.css';
import logoPng from './Images/png/Palatial-Logo_White 1.png';

function App() {
  const [userName, setUserName] = useState('');
  const [firstTimeUser, setFirstTimeUser] = useState(null);
  const [consentAccepted, setConsentAccepted] = useState(false);
  const [device, setDevice] = useState('Desktop');

  useEffect(() => {
    if (isMobile && !isIPad13 ) {
      setDevice('Mobile');
    } else if (isIPad13 || isTablet) {
      setDevice('Tablet');
    } else if (isDesktop) {
      setDevice('Desktop');
    }

    if (isMobile || isTablet || isIPad13) {
      document.body.style.height = `${window.innerHeight}px`;
      window.addEventListener('touchmove', function (event) {
        event.preventDefault();
      }, { passive: false });
    }
  }, []);

  const handleClick = (isFirstTime) => {
    setFirstTimeUser(isFirstTime);
  };

  const handleConsent = () => {
    setConsentAccepted(!consentAccepted);
  };

  return (
    <div className="App">
      <div className="PopUp">
        <div className='PopUpContent'>
        <div className="Logo">
          <img src={logoPng} alt='logo'/>
        </div>
        <div className="inputPrompt">
          <p>ENTER YOUR NAME</p>

            <input 
              className="userNameInput"
              type="text" 
              value={userName} 
              onChange={(e) => setUserName(e.target.value)} 
              required
            />

        </div>
        <div className="firstTimeToggle">
          <p>IS THIS YOUR FIRST TIME USING PALATIAL?</p>
          <div className="toggleButtons">
          <button className="yesButton" onClick={() => handleClick(true)}>YES</button>
          <button className="noButton" onClick={() => handleClick(false)}>NO</button>
          </div>
        </div>
        <div className="consentCTA">
          <div className="consentCheckBox" onClick={handleConsent}>
            <input type="checkbox" checked={consentAccepted} readOnly />
            <p>By clicking this box, Iâ€™m accepting the Terms and Conditions of using this platform.</p>
          </div>
        </div>
        </div>
      </div>
      <p>You are on a {device} device using {browserName} on {osName}.</p>
    </div>
  );
}

export default App;
