import React, { useState, useEffect, useRef } from 'react';
import { isDesktop, isIPad13, isTablet, isMobile, osName, browserName } from 'react-device-detect';
import './App.css';
import backgroundVideo from './Video/BackgroundVideo.mp4';

function App() {
  const [device, setDevice] = useState('Desktop');
  const videoRef = useRef(null);
  const [playingForward, setPlayingForward] = useState(true);

  useEffect(() => {
    if (isMobile && !isIPad13 ) {
      setDevice('Mobile');
    } else if (isIPad13 || isTablet) {
      setDevice('Tablet');
    } else if (isDesktop) {
      setDevice('Desktop');
    }

    if (isMobile || isTablet || isIPad13) {
      document.body.style.height = `${window.innerHeight}px`;
      window.addEventListener('touchmove', function (event) {
        event.preventDefault();
      }, { passive: false });
    }
  }, []);

  useEffect(() => {
    const videoElement = videoRef.current;

    const handleVideoEnd = () => {
      if (playingForward) {
        videoElement.currentTime = 0;
        videoElement.play();
      } else {
        videoElement.currentTime = videoElement.duration;
        videoElement.playbackRate = -1;
        videoElement.play();
      }
      setPlayingForward(!playingForward);
    };

    videoElement.addEventListener('ended', handleVideoEnd);
    return () => {
      videoElement.removeEventListener('ended', handleVideoEnd);
    };
  }, [playingForward]);

  const style = (isMobile || isTablet || isIPad13 ) ? { height: '100%', overflow: 'hidden' } : {};

  return (
    <div className="App">
      <video autoPlay loop muted
        style={{
          position: "absolute",
          width: "100%",
          height: "100%",
          left: "50%",
          top: "50%",
          objectFit: "cover",
          transform: "translate(-50%, -50%)",
          zIndex: "-1"
        }}
      >
        <source src={backgroundVideo} type="video/mp4" />
      </video>

      <p>You are on a {device} device using {browserName} on {osName}.</p>
    </div>
  );
}

export default App;
