import React, { useState, useEffect, useRef } from 'react';
import { isDesktop, isIPad13, isTablet, isMobile, osName, browserName } from 'react-device-detect';
import './App.css';
import logoPng from './Images/png/Palatial-Logo_White 1.png';

const useDeviceDetect = () => {
  const [device, setDevice] = useState('Desktop');

  useEffect(() => {
    if (isMobile && !isIPad13) {
      setDevice('Mobile');
    } else if (isIPad13 || isTablet) {
      setDevice('Tablet');
    } else if (isDesktop) {
      setDevice('Desktop');
    }
  }, []);

  return { device };
};

function ProgressBar({ progress }) {
  return (
    <div className="progressBar">
      <div className="progressBarProgress" style={{ width: `${progress}%` }} />
    </div>
  );
}

function App() {
  const { device } = useDeviceDetect();
  const [userName, setUserName] = useState('');
  const [firstTimeUser, setFirstTimeUser] = useState(null);
  const [consentAccepted, setConsentAccepted] = useState(false);
  const [progress, setProgress] = useState(0);
  const [step, setStep] = useState(0);
  const loadingSteps = ['Loading assets', 'Setting up', 'Connecting to server', 'Finalizing', 'Done']; // Add your loading steps here
  const [visibleStep, setVisibleStep] = useState(loadingSteps[0]);

  const stepTimeoutRef = useRef();


  //Device detection logic
  useEffect(() => {
    if (isMobile || isTablet || isIPad13) {
      document.body.style.height = `${window.innerHeight}px`;
      const preventScroll = event => {
        event.preventDefault();
      };
      window.addEventListener('touchmove', preventScroll, { passive: false });

      return () => {
        window.removeEventListener('touchmove', preventScroll);
      };
    }
  }, []);

  // progress bar animation
  useEffect(() => {

    let progressInterval = setInterval(() => {
      setProgress((prev) => prev + 20 >= 100 ? 0 : prev + 20);
      if (step + 1 >= loadingSteps.length) {
        setTimeout(() => {
          setStep(0);
          setVisibleStep(loadingSteps[0]);
        }, 500); // We're delaying the reset of the step and text
      } else {
        setStep((prev) => prev + 1);
        setVisibleStep(loadingSteps[step + 1]);
      }
    }, 1000);
    return () => clearInterval(progressInterval);
  }, []);

  useEffect(() => {
    stepTimeoutRef.current && clearTimeout(stepTimeoutRef.current);
    if (progress >= (step + 1) * 20) {
      stepTimeoutRef.current = setTimeout(() => {
        setStep(step + 1);
      }, 100);
    }
  }, [progress, step]);



  const handleClick = isFirstTime => {
    setFirstTimeUser(isFirstTime);
  };

  const handleConsent = () => {
    setConsentAccepted(!consentAccepted);
  };

  return (
    <div className="App">
      <div className="PopUp">
        <div className='PopUpContent'>
          <div className="Logo">
            <img src={logoPng} alt='logo'/>
          </div>
          <div className="inputPrompt">
            <p>ENTER YOUR NAME</p>
            <input
              className="userNameInput"
              type="text"
              value={userName}
              onChange={(e) => setUserName(e.target.value)} 
              required
            />
          </div>
          <div className="firstTimeToggle">
            <p>IS THIS YOUR FIRST TIME USING PALATIAL? </p>
            <div className="toggleButtons">
              <button className="yesButton" onClick={() => handleClick(true)}>YES</button>
              <button className="noButton" onClick={() => handleClick(false)}>NO</button>
            </div>
          </div>
          <div className="consentCTA">
            <div className="consentCheckBox" onClick={handleConsent}>
              <input type="checkbox" checked={consentAccepted} readOnly />
              <p>By clicking this box, I’m accepting the Terms and Conditions of using this platform.</p>
            </div>
          </div>
        </div>
      </div>
      <ProgressBar progress={progress} />
      <div className="loadingStep">
        {visibleStep}
      </div>
      <p>You are on a {device} device using {browserName} on {osName}.</p>
    </div>
  );
}

export default App;
