import { DataChannelController } from "../DataChannel/DataChannelController";
import { FakeTouchController } from "./FakeTouchController";
import { KeyboardController } from "./KeyboardController";
import { MouseController } from "./MouseController";
import { ITouchController } from "./ITouchController";
import { TouchController } from "./TouchController";
import { GamePadController } from "./GamepadController";
import { ControlSchemeType } from "../Config/Config";
import { VideoPlayerMouseLockedEvents } from "../VideoPlayer/VideoPlayerMouseLockedEvents";
import { VideoPlayerMouseHoverEvents } from "../VideoPlayer/VideoPlayerMouseHoverEvents";
import { GyroController } from "./GyroController";
import { IVideoPlayer } from "../VideoPlayer/IVideoPlayer";
import { IVideoPlayerMouseInterface } from "../VideoPlayer/VideoPlayerMouseInterface";
import { Logger } from "../Logger/Logger";
import { UeDescriptorUi } from "../UeInstanceMessage/UeDescriptorUi";

/**
 * Class for handling inputs for mouse and keyboard   
 */
export class InputController {

    videoElementProvider: IVideoPlayer;
    dataChannelController: DataChannelController;
    keyboardController: KeyboardController;
    mouseController: MouseController;
    touchController: ITouchController
    fakeTouchController: FakeTouchController;
    gamePadController: GamePadController;
    gyroController: GyroController;
    ueDescriptorUi: UeDescriptorUi;

    /**
     * 
     * @param dataChannelController - the data channel controller
     */
    constructor(dataChannelController: DataChannelController, ueDescriptorUi: UeDescriptorUi, videoElementProvider: IVideoPlayer) {
        this.dataChannelController = dataChannelController;
        this.videoElementProvider = videoElementProvider;
	this.ueDescriptorUi = ueDescriptorUi;
    }

    /**
     * registers browser key events  
     * @param suppressBrowserKeys - option to suppress browser keys 
     */
    registerKeyBoard(suppressBrowserKeys: boolean) {
        Logger.Log(Logger.GetStackTrace(), "Register Keyboard Events", 7);
        this.keyboardController = new KeyboardController(this.dataChannelController, this.ueDescriptorUi, suppressBrowserKeys);
        this.keyboardController.registerKeyBoardEvents();
    }

    /**
     * register mouse events based on a control type 
     * @param controlScheme - if the mouse is either hovering or locked 
     */
    registerMouse(controlScheme: ControlSchemeType) {
        Logger.Log(Logger.GetStackTrace(), "Register Mouse Events", 7);

        // casting these as any as they do not have the moz attributes we require
        let videoElement = this.videoElementProvider.getVideoElement() as any;
        let videoInputBindings: IVideoPlayerMouseInterface;

        this.mouseController = new MouseController(this.dataChannelController, this.videoElementProvider);

        switch (controlScheme) {
            case ControlSchemeType.LockedMouse:

                videoInputBindings = new VideoPlayerMouseLockedEvents(this.videoElementProvider, this.mouseController);

                videoElement.onclick = (event: MouseEvent) => this.videoElementProvider.setClickActions(event);

                document.addEventListener('pointerlockchange', () => videoInputBindings.handleLockStateChange(), false);
                document.addEventListener('mozpointerlockchange', () => videoInputBindings.handleLockStateChange(), false);

                break
            case ControlSchemeType.HoveringMouse:
                videoInputBindings = new VideoPlayerMouseHoverEvents(this.mouseController);

                // set the onclick to null if the input bindings were previously set to pointerlock
                videoElement.onclick = null;

                document.addEventListener("mousemove", (mouseEvent) => videoInputBindings.handleMouseMove(mouseEvent), { passive: false });
                document.addEventListener("wheel", (mouseEvent) => videoInputBindings.handleMouseWheel(mouseEvent), { passive : false });

                videoElement.onmousedown = (mouseEvent: MouseEvent) => videoInputBindings.handleMouseDown(mouseEvent);
                videoElement.onmouseup = (mouseEvent: MouseEvent) => videoInputBindings.handleMouseUp(mouseEvent);

                break
            default:
                Logger.Info(Logger.GetStackTrace(), "unknown Control Scheme Type Defaulting to Locked Mouse Events");
                break
        }
    }

    /**
     * register touch events 
     * @param fakeMouseTouch - the faked mouse touch event 
     * @param playerElement - the player elements DOM 
     */
    registerTouch(fakeMouseTouch: boolean, playerElement: HTMLVideoElement) {
        Logger.Log(Logger.GetStackTrace(), "Registering Touch", 6);
        if (fakeMouseTouch) {
	    //(<HTMLVideoElement>playerElement.getElementsByTagName("video")[0])
            this.touchController = new FakeTouchController(this.dataChannelController, playerElement);
        } else {
            this.touchController = new TouchController(this.dataChannelController, playerElement, this.videoElementProvider);
        }
    }

    /**
     * registers a gamepad 
     */
    registerGamePad() {
        Logger.Log(Logger.GetStackTrace(), "Register Game Pad", 7);
        this.gamePadController = new GamePadController(this.dataChannelController);


    }

    /**
     * registers a gyro device 
     */
    registerGyro() {
        this.gyroController = new GyroController(this.dataChannelController);

    }

}

import { SpecialKeyCodes } from "./SpecialKeyCodes";
import { DataChannelController } from "../DataChannel/DataChannelController";
import { UeInputKeyboardMessage } from "../UeInstanceMessage/UeInputKeyboardMessage";
import { UeDescriptorUi } from "../UeInstanceMessage/UeDescriptorUi";
import { Logger } from "../Logger/Logger";

/**
 * Handles the Keyboard Inputs for the document
 */
export class KeyboardController {
    ueInputKeyBoardMessage: UeInputKeyboardMessage;
    ueDescriptorUi: UeDescriptorUi;
    suppressBrowserKeys: boolean;

    /**
     * 
     * @param dataChannelController - Data Channel Controller
     * @param suppressBrowserKeys - Suppress Browser Keys
     */
    constructor(dataChannelController: DataChannelController, ueDescriptorUi: UeDescriptorUi, suppressBrowserKeys: boolean) {
        this.ueInputKeyBoardMessage = new UeInputKeyboardMessage(dataChannelController);
	this.ueDescriptorUi = ueDescriptorUi;
        this.suppressBrowserKeys = suppressBrowserKeys;
    }

    /**
     * Registers document keyboard events with the controller
     */
    registerKeyBoardEvents() {
        document.onkeydown = (ev: KeyboardEvent) => this.handleOnKeyDown(ev);
        document.onkeyup = (ev: KeyboardEvent) => this.handleOnKeyUp(ev);

        //This has been deprecated as at Jun 13 2021
        document.onkeypress = (ev: KeyboardEvent) => this.handleOnKeyPress(ev);
    }

    /**
     * Handles When a key is down
     * @param keyboardEvent - Keyboard event 
     */
    handleOnKeyDown(keyboardEvent: KeyboardEvent) {
	if (keyboardEvent.ctrlKey) {
		if (keyboardEvent.key == 'v') {
			const self = this;
			navigator.clipboard.readText().then(text => {
				for (let i = 0; i < text.length; i++) {
					const char = text.charAt(i);
					const key = char.charCodeAt(0);
					self.ueInputKeyBoardMessage.sendKeyDown(key, keyboardEvent.repeat);
					document.onkeypress(new KeyboardEvent("keypress", { charCode: key }));
				}
			}).catch(err => {
				console.error('Failed to read clipboard contents: ', err);
			});
		}
		if (keyboardEvent.key == 'c') {
			this.ueDescriptorUi.sendUiConsoleInteraction("requestInputSelection");
		}
		if (this.getKeycode(keyboardEvent) == SpecialKeyCodes.backSpace) {
			// clear input box
		}
	} else {
		const key = keyboardEvent.key;
		if (key == 'Unidentified') {
			return;
		}
		this.ueInputKeyBoardMessage.sendKeyDown(this.getKeycode(keyboardEvent), keyboardEvent.repeat);

		// backSpace is not considered a keypress in JavaScript but we need it
		// to be so characters may be deleted in a UE4 text entry field.
		if (keyboardEvent.keyCode === SpecialKeyCodes.backSpace) {
			document.onkeypress(new KeyboardEvent("keypress", { charCode: SpecialKeyCodes.backSpace }));
		}
	}
	if (this.suppressBrowserKeys && this.isKeyCodeBrowserKey(keyboardEvent.keyCode)) {
		keyboardEvent.preventDefault();
	}
    }

    /**
     * handles when a key is up
     * @param keyboardEvent - Keyboard event
     */
    handleOnKeyUp(keyboardEvent: KeyboardEvent) {
        Logger.Log(Logger.GetStackTrace(), "handleOnKeyUp", 6);
        this.ueInputKeyBoardMessage.sendKeyUp(this.getKeycode(keyboardEvent));

        if (this.suppressBrowserKeys && this.isKeyCodeBrowserKey(keyboardEvent.keyCode)) {
            keyboardEvent.preventDefault();
        }
    }

    /**
     * Handles when a key is press
     * @param keyboard - Keyboard Event
     */
    handleOnKeyPress(keyboard: KeyboardEvent) {
        Logger.Log(Logger.GetStackTrace(), "handleOnkeypress", 6);
        this.ueInputKeyBoardMessage.sendKeyPress(keyboard.charCode);
    }

    /**
     * Gets the Keycode of the Key pressed
     * @param keyboardEvent - Key board Event
     * @returns the key code of the Key
     */
    getKeycode(keyboardEvent: KeyboardEvent) {
        //Need to move this to a newer version using keyboard event location. as keyboardEvent.keycode is deprecated

        if (keyboardEvent.keyCode === SpecialKeyCodes.shift && keyboardEvent.code === 'ShiftRight') return SpecialKeyCodes.rightShift;
        else if (keyboardEvent.keyCode === SpecialKeyCodes.control && keyboardEvent.code === 'ControlRight') return SpecialKeyCodes.rightControl;
        else if (keyboardEvent.keyCode === SpecialKeyCodes.alt && keyboardEvent.code === 'AltRight') return SpecialKeyCodes.rightAlt;
        else return keyboardEvent.keyCode;
    }

    /**
     * Browser keys do not have a charCode so we only need to test keyCode.
     */
    isKeyCodeBrowserKey(keyCode: number) {
        // Function keys or tab key.
	if (keyCode == 116 || keyCode == 122) return false;
        return keyCode >= 112 && keyCode <= 123 || keyCode === 9;
    }
}
